const completed = $('Get Completed').all();
const queue = $('Get Queued').all();

// --- Calculate average rating ---
let totalRating = 0;
let ratedCount = 0;
let maxRating = 0;
const topRatedArtists = [];

for (const artist of completed) {
  const rating = artist.json.property_rating;
  if (rating) {
    const stars = (rating.match(/‚≠ê/g) || []).length;
    totalRating += stars;
    ratedCount++;

    if (stars > maxRating) {
      maxRating = stars;
    }
  }
}

// --- Collect all artists with highest rating ---
for (const artist of completed) {
  const rating = artist.json.property_rating;
  if (rating) {
    const stars = (rating.match(/‚≠ê/g) || []).length;
    if (stars === maxRating && maxRating > 0) {
      topRatedArtists.push(artist.json.property_artist_name);
    }
  }
}

const avgRating = ratedCount > 0 ? (totalRating / ratedCount).toFixed(1) : 0;

// --- Collect genres and frequency ---
const genreCount = {};
const uniqueGenres = [];

for (const artist of completed) {
  const genres = artist.json.property_genre;
  if (Array.isArray(genres)) {
    for (const g of genres) {
      if (!uniqueGenres.includes(g)) uniqueGenres.push(g);
      genreCount[g] = (genreCount[g] || 0) + 1;
    }
  }
}

// --- Determine most common genre(s) ---
let mostCommonGenres = [];
let maxCount = 0;

for (const [genre, count] of Object.entries(genreCount)) {
  if (count > maxCount) {
    mostCommonGenres = [genre];
    maxCount = count;
  } else if (count === maxCount) {
    mostCommonGenres.push(genre);
  }
}

// --- Output summary ---
return {
  completed_count: completed.length,
  queue_count: queue.length,
  total_artists: completed.length + queue.length,
  average_rating: avgRating,
  genres_explored: uniqueGenres.length,
  genres_list: uniqueGenres.join(', '),
  most_common_genres: mostCommonGenres.join(', ') || 'N/A',
  most_common_genre_count: maxCount,
  highest_rating: maxRating || 'N/A',
  highest_rated_artists: topRatedArtists.length > 0 ? topRatedArtists.join(', ') : 'N/A'
};

